import React, { useState } from 'react';
import RemoveIcon from '@material-ui/icons/Remove';
import HighlightOffIcon from '@material-ui/icons/HighlightOff';
import Button from '@material-ui/core/Button';

import './incdec.css';
const Inc = ()=>{
    const [num,setNum] = useState(0);
    const incc = () => {
        setNum(num+1);
    }
    const dec = () => {
        if(num > 0){
        setNum(num-1);
        }
        else{
            setNum(0);
            alert("Sorry We Can't Go Less Then Zero So Increment the Number ");
        }
    }
    return(
    <>
        <div className="main_div">
            <div className="center_div">
                <h1><span className="spa">{num} </span></h1>
                <div className="button_div">
                    <button onClick={incc} className="b1">+</button>
                    <Button onClick={dec} className="b2"> <RemoveIcon style={{ color:"steelblue" , fontSize: 40 }}/> </Button>
                </div>
            </div>
        </div>
    </>
    );
}

export default Inc;
