import React, { useState } from 'react';
import './hoo.css';
const Form = () =>{
    const [name, setName] = useState();
    const Input = () =>{
        setName(this.target.value);

    }
    const [fullname,setFullName] = useState();
    const Full = () =>{
        setFullName(name);
    }
    
    return(
        <>
            <div>
                <h1 className="h">Hello {fullname}</h1>
                <input type="text" placeholder="Enter Your Name" onChange={Input} value={name} />
                <button className="but" onClick={Full}>Submit </button>
            </div>
        </>
    )


}

export default Form;
