import React from 'react';
import { useLocation, useParams } from 'react-router-dom';

const User = () => {
    const location = useLocation();
    const {fname, lname} = useParams();
    return(
        <>
        <h1>Hello, i am {fname} {lname} Page</h1>
        <br/>
        <h1>Hello {fname} {lname} your location is : {location.pathname}</h1>
        <br/>
        {location.pathname === `/user/vijaya/kurapati` ? (
            <button style={{color:'red', fontSize:30}} onClick={()=>alert("Your Granted")}>Click Here</button>
        ):null}
        </>
    );
}

export default User;
