import React from 'react';
import { useHistory, useLocation, useParams } from 'react-router-dom';

const User = () => {
    const location = useLocation();
    const {fname, lname} = useParams();
    const history = useHistory();
    return(
        <>
        <h1>Hello, i am {fname} {lname} Page</h1>
        <br/>
        <h1>Hello {fname} {lname} your location is : {location.pathname}</h1>
        <br/>
        {location.pathname === `/user/sunil/kurapati` ? (
            <button style={{color:'red', fontSize:30,borderRadius:12,background:'yellow',border:'3 solid blue',float:'right'}} onClick={()=> history.goBack()}> 🔙 </button>
        ):null}
        <br/>
        <br/>
        <br/>
        {location.pathname === `/user/sunil/kurapati` ? (
            <button style={{color:'red', fontSize:30,borderRadius:12,background:'yellow',border:'3 solid blue'}} onClick={()=> history.push('/')}> 🏠</button>
        ):null}
        </>
    );
}

export default User;
