import React, { useState } from 'react';
import './hoo.css';
const Form = () =>{
    const [name, setName] = useState();
    const [fullname,setFullName] = useState();
    const [name1, setName1] = useState();
    const [fullname1,setFullName1] = useState();


    const Input = (ok) =>{
        setName(ok.target.value);

    }
   
    const Input1 = (ok) =>{
        setName1(ok.target.value);
    }

    const Full1 = (event) =>{
        event.preventDefault();
        setFullName(name);
        setFullName1(name1);
    }
    
    return(
        <>
        <form onSubmit={Full1}>
            <div>
                <h1 className="h">Hello {fullname } your Password is  {fullname1}</h1>
                <input type="text" placeholder="Enter Your Name" onChange={Input} value={name} required />
                <input type="password" placeholder="Enter Your Password" onChange={Input1} value={name1} required />

                <button className="but" >Submit üëç </button>
            </div>
            </form> 
        </>
    )


}

export default Form;
