import React, { useState } from 'react';
import './hoo.css';
const Form = () =>{
    const [fullname, setName] = useState({
        fname:"",
        lname:"",
    });
  
const input = (event) => {
    const value = event.target.value;
    const name = event.target.name;

    setName((prev) => {

        if(name === "fname"){
        return{
            fname:value,
            lname:prev.lname,
        };
        }
        else if(name ==="lname")
        {
            return{
            fname:prev.fname,
            lname:value,
        }
    }
    });

}


const onsubmits = (event) =>{
    event.preventDefault();


}
    
    return(
        <>
        <form  onSubmit={onsubmits}>
            <div>
                <h1 className="h">Hello your fullname is {fullname.fname} {fullname.lname}  </h1>
                <input type="text" placeholder="Enter Your Name" name="fname" onChange={input} />
                <input type="text" placeholder="Enter Your SirName" name="lname" onChange={input} />
                <input type="email" placeholder="Enter Your email" name="email" onChange={input} />
                <input type="text" placeholder="Enter Your Mobile_Number" name="mobile" onChange={input} />

                <button className="but" >Submit üëç </button>
            </div>
            </form> 
        </>
    )


}

export default Form;
