import React, { useState } from 'react';
import './incdec.css';
const Inc = ()=>{
    const [num,setNum] = useState(0);
    const incc = () => {
        setNum(num++);
    }
    const dec = () => {
        setNum(num--);
    }
    return(
    <>
        <div className="main_div">
            <div className="center_div">
                <h1>{num}</h1>
                <div className="button_div">
                    <button onClick={incc} className="b1">+</button>
                    <button onClick={dec} className="b2">-</button>
                </div>
            </div>
        </div>
    </>
    );
}

export default Inc;
