import React, { useState } from 'react';
import './hoo.css';
const Form = () =>{
    const [name, setName] = useState();
    const [fullname,setFullName] = useState();
    const [name1, setName1] = useState();
    const [fullname1,setFullName1] = useState();


    const Input = (ok) =>{
        setName(ok.target.value);

    }
   
    const Input1 = (ok) =>{
        setName1(ok.target.value);
    }

    const Full1 = () =>{
        setFullName(name);
        setFullName1(name1);
    }
    
    return(
        <>
            <div>
                <h1 className="h">Hello {fullname} {fullname1}</h1>
                <input type="text" placeholder="Enter Your Name" onChange={Input} value={name} />
                <input type="text" placeholder="Enter Your Name" onChange={Input1} value={name1} />

                <button className="but" onClick={Full1}>Submit üëç </button>
            </div>
        </>
    )


}

export default Form;
